import { AppBar, Box, Button, TextField, Typography, Container, Link, Paper } from "@mui/material";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import http from "../../../http";
import IRestaurante from "../../../interfaces/IRestaurante";

import {Link as RouterLink} from 'react-router-dom'

const FormularioRestaurante = () => {
  const parametros = useParams()

  useEffect(() => {
    if (parametros.id) {
      http.get<IRestaurante>(`restaurantes/${parametros.id}/`)
        .then(resposta => setNomeRestaurante(resposta.data.nome))
    }
  }, [parametros])

  const [nomeRestaurante, setNomeRestaurante] = useState('')

  const aoSubmeterForm = (evento: React.FormEvent<HTMLFormElement>) => {
    evento.preventDefault() // não tenha comportamento de recarregar a pagina

    if (parametros.id) {
      http.put(`restaurantes/${parametros.id}/`, {
        nome: nomeRestaurante
      })
        .then(() => {
          alert("Restaurante atualizado com sucesso!")
        })
    } else {
      http.post('restaurantes/', {
        nome: nomeRestaurante
      })
        .then(() => {
          alert("Restaurante cadastrado com sucesso!")
        })
    }

  }


  return (
      <Box>
        <Container maxWidth="lg" sx={{ mt: 1 }}>
          <Paper sx={{ p: 2 }}>

            {/* Conteudo da pagina */}
            <Box sx={{ display: 'flex', flexDirection: "column", alignItems: "center", flexGrow: 1}}>
              <Typography component="h1" variant="h6"> Formulario de restaurantes </Typography>
              <Box component="form" sx={{ width: '100%'}} onSubmit={aoSubmeterForm}>
                <TextField
                  value={nomeRestaurante}
                  onChange={evento => setNomeRestaurante(evento.target.value)}
                  id="standard-basic"
                  label="Nome do Restaurante"
                  variant="standard"
                  fullWidth
                  required
                />
                <Button sx={{ marginTop: 1 }} type="submit" fullWidth variant="outlined">Salvar</Button>
              </Box>
            </Box>
          </Paper>

        </Container>
      </Box>
  )
}

export default FormularioRestaurante
